  &__content {
    grid-column: 1/6;
    grid-row: 2/3;
    @media (min-width: 600px) {
      grid-column: 2/6;
      grid-row: 2/4;
    }
  }

  &--edit{
    &>.comment__content{
      display: none;
    }
    &> .new-comment--update{
      display: grid;
    }
  }

  .new-comment--update {
    grid-column: 1/8;
    grid-row: 2/3;
    @media (min-width: 600px) {
      grid-column: 2/8;
      grid-row: 2/4;
