    row-gap: unset;
    column-gap: 1rem;
  }
  &--update {
    display: none;
    grid-template-columns: unset;
  }
  &--reply{
    display: none;
  }
  &__input {
    grid-column: 1/3;
    grid-row: 1/2;
    @media (min-width: 600px) {
      grid-column: 2/3;
      grid-row: 1/3;
    }
  }
  &--update {
    .new-comment__input {
      grid-column: 1/3;
    }
  }

  .avatar--new-comment {
    grid-column: 1/2;
    @media (min-width: 600px) {
      grid-row: 1/2;
    }
  }

  &__submit {
    grid-column: 2/3;
    @media (min-width: 600px) {
      grid-column: 3/4;
      grid-row: 1/2;
    }
  }
  &--update {
    & > .new-comment__send--update {
      grid-column: 2/3;
    }
  }
}

