@use "utilities/variables" as *;
:root {
  @each $type, $size in $layout {
    --layout-#{$type}: #{$size};
  }
  @each $color, $name in map-get($color, primary) {
    --color-#{$color}: #{$name};
  }
  @each $color, $name in map-get($color, neutral) {
    --color-#{$color}: #{$name};
  }
  @each $cat, $value in $font {
    --font-#{$cat}: #{$value};
  }
  @each $cat, $value in $font-weight {
    --font-weight-#{$cat}: #{$value};
  }
}

html{
    font-size: var(--font-size);
    font-family: var(--font-family);
}

