@use "utilities/functions" as *;
@use "utilities/variables" as *;

.comment {
  display: grid;
  grid-template-columns: rem(32px) rem(125px) repeat(5, auto);
  row-gap: rem(20px);
  @media (min-width: 600px) {
    grid-template-columns: rem(56px) rem(32px) rem(125px) repeat(4, auto);
    grid-template-rows: rem(32px) rem(67px) auto;
    row-gap: unset;
  }
  align-items: center;

  .avatar--comment {
    grid-column: 1/2;
    grid-row: 1/2;
    @media (min-width: 600px) {
      grid-column: 2/3;
    }
  }

  .username {
    grid-column: 2/3;
    grid-row: 1/2;
    @media (min-width: 600px) {
      grid-column: 3/4;
    }
  }

  &__createdAt {
    grid-column: 3/6;
    grid-row: 1/2;
    @media (min-width: 600px) {
      grid-column: 4/5;
    }
  }

  &__content {
    grid-column: 1/6;
    grid-row: 2/3;
    @media (min-width: 600px) {
      grid-column: 2/6;
      grid-row: 2/4;
    }
  }

  &--edit{
    &>.comment__content{
      display: none;
    }
    &> .new-comment--update{
      display: grid;
    }
  }

  .new-comment--update {
    grid-column: 1/8;
    grid-row: 2/3;
    @media (min-width: 600px) {
      grid-column: 2/8;
      grid-row: 2/4;
    }

  &--edit{
    .new-comment--update{
      grid-row: 3/4;
    }
  }

  }

  .vote {
    grid-column: 1/3;
    grid-row: 3/4;
    @media (min-width: 600px) {
      grid-column: 1/2;
      grid-row: 1/3;
    }
  }
  &--edit{
    .vote{
      grid-row: 3/4;
      @media (min-width: 600px) {
        grid-row: 1/3;
      }
    }

  }

  .CRUD-container {
    grid-column: 4/6;
    grid-row: 3/4;
    @media (min-width: 600px) {
      grid-row: 1/2;
    }
  }
  &--edit{
    .CRUD-container{
      grid-column: 4/8;
      grid-row: 3/4;
      @media (min-width: 600px) {
      grid-row: 1/2;
    }
    }

  }

}

$ruler-column-size: clamp(rem(16px), vw(16px), rem(42px));

.reply-wrapper {
  display: grid;
  row-gap: clamp(rem(16px), vw(16px), rem(24px));
  column-gap: $ruler-column-size;
  @media (min-width: $ruler-break) {
    grid-template-columns: $ruler-column-size auto;
  }

  &__ruler {
    grid-column: 1/2;
    grid-row: 1/ -1;
  }
  .comment--reply {
    grid-column: 2/3;
  }
  .new-comment--reply{
    grid-column: 2/3;
  }
}

.new-comment {
  display: grid;
  row-gap: rem(17px);
  @media (min-width: 600px) {
    grid-template-columns: rem(32px) auto rem(104px);
    row-gap: unset;
    column-gap: 1rem;
  }
  &--update {
    display: none;
    grid-template-columns: unset;
  }
  &--reply{
    display: none;
  }
  &__input {
    grid-column: 1/3;
    grid-row: 1/2;
    @media (min-width: 600px) {
      grid-column: 2/3;
      grid-row: 1/3;
    }
  }
  &--update {
    .new-comment__input {
      grid-column: 1/3;
    }
  }

  .avatar--new-comment {
    grid-column: 1/2;
    @media (min-width: 600px) {
      grid-row: 1/2;
    }
  }

  &__submit {
    grid-column: 2/3;
    @media (min-width: 600px) {
      grid-column: 3/4;
      grid-row: 1/2;
    }
  }
  &--update {
    & > .new-comment__send--update {
      grid-column: 2/3;
    }
  }
}

